import * as log4js from 'log4js';

log4js.configure({
  appenders: {
    console: { type: 'console' },
    file: { type: 'file', filename: 'logs/log.log' }, // Nombre y ubicación del archivo de registro
  },
  categories: {
    default: { appenders: ['console', 'file'], level: 'debug' }, // Nivel de registro
  },
});

export default log4js;
